div.script:

$("#registrasiForm").submit(function(event) {
	console.log("Tes")
	event.preventDefault();
	var data    = $('#registrasiForm').serialize();                
	$.ajax({
		type: "POST",
		data: data,
		url: "/register/save",
		success: function(toni) {
			console.log(toni)
			if (toni.info.status == 0) {            
			Swal.fire(toni.info.intro,toni.info.pesan,toni.info.type)                    
		}else if (toni.info.status == 3) {
			Swal.fire(toni.info.intro,toni.info.pesan,toni.info.type)
			.then((beres) => {
			window.location.assign("/evoting"); 
			});                
		}   
		else {            
			Swal.fire(toni.info.intro,toni.info.pesan,toni.info.type)
			.then((beres) => {
			window.location.assign("/login"); 
			});                
		}
		}
	});
});


$("#loginForm").submit(function(event) {
	console.log("Tes")
	event.preventDefault();
	var data    = $('#loginForm').serialize();                
	$.ajax({
		type: "POST",
		data: data,
		url: "/login_check",
		success: function(toni) {
			console.log(toni)
			if (toni.info.status == 0) {            
			Swal.fire(toni.info.intro,toni.info.pesan,toni.info.type)                    
		}   
		else {            
			Swal.fire(toni.info.intro,toni.info.pesan,toni.info.type)
			.then((beres) => {
			window.location.assign("/formpertanyaan"); 
			});                
		}
		}
	});
});


	$("#reset").submit(function(event) {
	console.log("Tes")
	event.preventDefault();
	var data = $('#reset').serialize();                
	$.ajax({
		type: "POST",
		data: data,
		url: "/reset_password",
		success: function(toni) {
			console.log(toni)
			if (toni.info.status == 0) {            
			Swal.fire(toni.info.intro,toni.info.pesan,toni.info.type)  
			window.location.assign("/resetpassword");                   
		}  
		else {            
			Swal.fire(toni.info.intro,toni.info.pesan,toni.info.type)
			.then((beres) => {
			window.location.assign("/login"); 
			});                
		}
		}
	});
});


